package com.coreservlets.demo2.bean;

/**
 * 带有 setter/getter 的 Java Bean
 */
public class Person {

    private String name;
    private String email;

    public Person() {
        // TODO Auto-generated constructor stub
    }

    public String getName() {
        return name;
    }

    public void setName(String name) {
        this.name = name;
    }

    public String getEmail() {
        return email;
    }

    public void setEmail(String email) {
        this.email = email;
    }


}
